import React, { Component } from 'react'
import NewsItem from './NewsItem'

export class News extends Component {
    constructor(){
        super();
        this.state = {
            page:1,
            articles: [],
            loading:false,
            totalResults:0
        }
    }
    async componentDidMount(){
        let url = "https://newsapi.org/v2/top-headlines?country=in&apiKey=7bf5600bfdaf4af78127f3f1f5dd2212&pagesize=20";
        let data = await fetch(url);
        let parsedData = await data.json();
        console.log(parsedData);
        this.setState({articles: parsedData.articles, totalResults: parsedData.totalResults})
    }
    previous= async ()=>{
        console.log("Previous Button is clicked");
        let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=7bf5600bfdaf4af78127f3f1f5dd2212&page=${this.state.page-1}&pagesize=20`;
        let data = await fetch(url);
        let parsedData = await data.json();
        console.log(parsedData);
        this.setState({articles: parsedData.articles, page:this.state.page-1})
    }
    next= async ()=>{
        
        console.log("next Button is clicked");
        let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&apiKey=7bf5600bfdaf4af78127f3f1f5dd2212&page=${this.state.page+1}&pagesize=20`;
        let data = await fetch(url);
        let parsedData = await data.json();
        console.log(parsedData);
        this.setState({articles: parsedData.articles, page:this.state.page+1})
    }
    
    render() {
        if(this.state.arcticles.length()===0){
            <div class="text-center">
  <div class="spinner-border" role="status">
    <span class="sr-only">Loading...</span>
  </div>
</div>
        }
    return (
        <>
        <div className="container my-3">
            This is a news component 
            <div className="row">
            {this.state.articles.map((element)=>{ 
                return( 
            <div className="col-md-4 my-2" key={element.url}>
        <NewsItem title={element.title} description={element.description} ImageUrl={element.urlToImage} NewsUrl={element.url}/>
            </div>
                )
            })};
            </div>
          
        </div>
        <div>
          <div className="container d-flex justify-content-between my-3 mx-2">
        <button type="button" disabled={this.state.page<=1} onClick={this.previous} class="btn btn-dark">&larr; Preview</button>
        <button type="button" disabled={this.state.page >= Math.ceil(this.state.totalResults/20)} onClick={this.next} class="btn btn-dark">Next &rarr;</button>
        </div>
        </div>
        </>
    )
  }
}

export default News
