import React, { Component } from 'react'
import NewsItem from './NewsItem'

export class News extends Component {
    constructor(){
        super();
        this.state = {
            articles: [],
            loading:false
        }
    }
   async componentDidMount(){
        let url = "https://newsapi.org/v2/top-headlines?country=in&apiKey=7bf5600bfdaf4af78127f3f1f5dd2212";
        let data = await fetch(url);
        let parsedData = await data.json();
        console.log(parsedData);
        this.setState({articles: parsedData.articles})
   }
 previous=()=>{
console.log("Previous Button is clicked");
}
 next=()=>{
    console.log("next Button is clicked")
}
    
    render() {
    return (
        <>
        <div className="container my-3">
            This is a news component 
            <div className="row">
            {this.state.articles.map((element)=>{ 
                return( 
            <div className="col-md-4 my-2" key={element.url}>
        <NewsItem title={element.title} description={element.description} ImageUrl={element.urlToImage} NewsUrl={element.url}/>
            </div>
                )
            })};
            </div>
          
        </div>
        <div>
          <div className="container d-flex justify-content-between my-3 mx-2">
        <button type="button" disabled={this.state.page<=1} onClick={this.previous} class="btn btn-dark">&larr; Preview</button>
        <button type="button" onClick={this.next} class="btn btn-dark">Next &rarr;</button>
        </div>
        </div>
        </>
    )
  }
}

export default News
