import React, { Component } from 'react'
import NewsItem from './NewsItem'

export class News extends Component {
    static defaultProps={
        country: 'in',
        pageSize:8
    }
Capitalize=(str)=>{
    return str.charAt(0).toUpperCase() + str.slice(1);
}
    constructor(props){
        super(props);
        this.state = {
            page:1,
            articles: [],
            loading:false,
            totalResults:0
        }
        document.title= `${this.Capitalize(this.props.category)}-NewsMonkey`;
    }

    updateNews= async()=>{
        let url = `https://newsapi.org/v2/top-headlines?category=${this.props.category}&country=${this.props.country}&apiKey=7bf5600bfdaf4af78127f3f1f5dd2212&page=${this.state.page}&pagesize=${this.props.pageSize}`;
        let data = await fetch(url);
        let parsedData = await data.json();
        console.log(parsedData);
        this.setState({articles: parsedData.articles})
    }
    async componentDidMount(){
        let url = `https://newsapi.org/v2/top-headlines?category=${this.props.category}&country=${this.props.country}&apiKey=7bf5600bfdaf4af78127f3f1f5dd2212&pagesize=${this.props.pageSize}`;
        let data = await fetch(url);
        let parsedData = await data.json();
        console.log(parsedData);
        this.setState({articles: parsedData.articles, totalResults: parsedData.totalResults})
    }
    previous= async ()=>{
        // console.log("Previous Button is clicked");
        // let url = `https://newsapi.org/v2/top-headlines?category=${this.props.category}&country=${this.props.country}&apiKey=7bf5600bfdaf4af78127f3f1f5dd2212&page=${this.state.page-1}&pagesize=${this.props.pageSize}`;
        // let data = await fetch(url);
        // let parsedData = await data.json();
        // console.log(parsedData);
        this.setState({page:this.state.page-1})
        this.updateNews();
    }
    next= async ()=>{
        
        // console.log("next Button is clicked");
        // let url = `https://newsapi.org/v2/top-headlines?category=${this.props.category}&country=${this.props.country}&apiKey=7bf5600bfdaf4af78127f3f1f5dd2212&page=${this.state.page+1}&pagesize=${this.props.pageSize}`;
        // let data = await fetch(url);
        // let parsedData = await data.json();
        // console.log(parsedData);
        this.setState({page:this.state.page+1})
        this.updateNews();
    }
    
    render() {
        if(this.state.articles.length===0){
            return(
            <>
            <div class="text-center">
  <div class="spinner-border" role="status">
    <span class="sr-only">Loading...</span>
  </div>
</div>
</>
);

 }

else{
    return(
        <>
        <div className="container my-3">
        <InfiniteScroll
          dataLength={this.state.items.length}
          next={this.fetchMoreData}
          hasMore={true}
          loader={<h4>Loading...</h4>}
        />
            <div className="row">
            {this.state.articles.map((element)=>{ 
                return( 
            <div className="col-md-4 my-2" key={element.url}>
        <NewsItem title={element.title} description={element.description} ImageUrl={element.urlToImage} NewsUrl={element.url} author={element.source.name} date={element.publishedAt}/>
            </div>
                )
            })};
            </div>
          
        </div>
        <div>
          <div className="container d-flex justify-content-between my-3 mx-2">
        <button type="button" disabled={this.state.page<=1} onClick={this.previous} class="btn btn-dark">&larr; Preview</button>
        <button type="button" disabled={this.state.page >= Math.ceil(this.state.totalResults/this.props.pageSize)} onClick={this.next} class="btn btn-dark">Next &rarr;</button>
        </div>
        </div>
        </>
    )
  }
}
}

export default News
